import React from 'react'
import { useFormField } from '../components/hooks/hooks'

const getImageForHouse = (house) => {
    switch (house) {
        case 'Hufflepuff':
            return 'data:image/jpeg;base64,'
        case 'Slytherin':
            return 'data:image/jpeg;base64,'
        case 'Gryffindor':
            return 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQHkEQ6hsHGuvGpjKfZ5XEv-yF3poqzcccMI-LNlGGE8fIlAwxL2w'
        case 'Ravenclaw':
            return 'data:image/jpeg;base64,'
        default:
            return 'data:image/jpeg;base64,'
    }
}

const Character = ({ name, house = 'Hogwarts', bloodStatus }) => (
    <div className="max-w-sm rounded overflow-hidden shadow-lg m-10 card">
        <img height="200" width="200" src={getImageForHouse(house)} alt={`Logo of ${house}`}/>
        <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{name}</div>
            <p className="text-grey-darker text-base">
                House: {house}
            </p>
        </div>
        <div className="px-6 py-4">
            <span
                className="inline-block bg-grey-lighter rounded-full px-3 py-1 text-sm font-semibold text-grey-darker mr-2">
                Blood: {bloodStatus || 'Hogwarts'}
            </span>
        </div>
    </div>
)

const Home = ({ wizards }) => {
    const field = useFormField('texto aqui')

    if (!wizards.length) return <h1>Loading...</h1>

    return (
      <>
        <input className="w-screen border-solid border-4 border-gray-600" type="text" {...field} />
        <div className="flex flex-wrap">
            {wizards.map((char, index) => <Character key={index} {...char} />)}
        </div>
      </>
    )
}

export default Home;
